#include <iostream>

static const char* input =
"";

bool all_different(size_t pos, size_t N)
{
    for (size_t i = 1; i < N; ++i)
        for (size_t j = i; j < N; ++j)
            if (input[pos-i] == input[pos-j-1])
                return false;

    return true;
}

size_t detect(size_t N)
{
    size_t ret = N;

    while(!all_different(ret, N))
        ++ret;

    return ret;
}

size_t part1()
{
    return detect(4);
}

size_t part2()
{
    return detect(14);
}

int main()
{
    std::cout << "Part 1: " << part1() << std::endl;
    std::cout << "Part 2: " << part2() << std::endl;

    return 0;
}
